import React, { useState } from "react";
import { View, Text, TextInput, StyleSheet, Alert } from "react-native";
import ActionButton from "../components/ActionButton";
import { sendMoney } from "../api/bankApi";

export default function SendMoneyScreen({ navigation }) {
  const [name, setName] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [amount, setAmount] = useState("");

  const handleSend = () => {
    if (!name || !accountNumber || !amount) {
      Alert.alert("Error", "Please fill all fields");
      return;
    }

    try {
      sendMoney(Number(amount));
      Alert.alert("Success", `Money sent to ${name}`);
      navigation.goBack();
    } catch (err) {
      Alert.alert("Error", err.message);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>Recipient Name</Text>
      <TextInput style={styles.input} value={name} onChangeText={setName} />

      <Text style={styles.label}>Account Number</Text>
      <TextInput
        style={styles.input}
        value={accountNumber}
        onChangeText={setAccountNumber}
        keyboardType="number-pad"
      />

      <Text style={styles.label}>Amount</Text>
      <TextInput
        style={styles.input}
        value={amount}
        onChangeText={setAmount}
        keyboardType="number-pad"
      />

      <ActionButton text="Send Money" color="#2196F3" onPress={handleSend} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: "#fff",
  },
  label: {
    marginTop: 15,
    fontWeight: "bold",
  },
  input: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 10,
    padding: 12,
    marginTop: 5,
  },
});
