import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Button, StyleSheet } from 'react-native';
import transactionsData from '../api/api.json'; // import local JSON

export default function TransactionsScreen() {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // Load transactions from JSON
    setTransactions(transactionsData.transactions);
  }, []);

  const addTransaction = () => {
    const newTransaction = {
      id: transactions.length + 1,
      title: `Transaction #${transactions.length + 1}`,
      amount: Math.floor(Math.random() * 100),
    };
    const updated = [...transactions, newTransaction];
    setTransactions(updated);

    // Optionally, if you want to save back to api.json in a real app,
    // you would need file system access (not possible in Expo Web),
    // but this works in-memory for now.
  };

  return (
    <View style={styles.container}>
      <Button title="Add Transaction" onPress={addTransaction} />
      <FlatList
        data={transactions}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text>{item.title}</Text>
            <Text>${item.amount}</Text>
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20 },
  item: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ddd',
  },
});
