import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert } from 'react-native';
import transactionsData from '../api/api.json';

export default function SendMoneyScreen({ navigation }) {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState('');

  const handleSend = () => {
    if (!name || !amount) {
      Alert.alert('Error', 'Please enter name and amount');
      return;
    }

    const newTransaction = {
      id: transactionsData.transactions.length + 1,
      name,
      amount: parseFloat(amount),
    };

    transactionsData.transactions.push(newTransaction); // store in local JSON (in-memory)
    Alert.alert('Success', `Sent $${amount} to ${name}`);

    navigation.goBack(); // go back to transactions
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>Recipient Name:</Text>
      <TextInput
        style={styles.input}
        value={name}
        onChangeText={setName}
        placeholder="Enter name"
      />

      <Text style={styles.label}>Amount:</Text>
      <TextInput
        style={styles.input}
        value={amount}
        onChangeText={setAmount}
        placeholder="Enter amount"
        keyboardType="numeric"
      />

      <Button title="Send" onPress={handleSend} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20 },
  label: { marginTop: 10 },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
});
