import React, { useState } from "react";
import { View, Text, TextInput, StyleSheet, Alert } from "react-native";
import ActionButton from "../components/ActionButton";
import { sendMoney } from "../api/bankApi";

export default function SendMoneyScreen({ goBack }) {
  const [name, setName] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [amount, setAmount] = useState("");

  const handleSend = () => {
    if (!name || !accountNumber || !amount) {
      Alert.alert("Error", "Fill all fields");
      return;
    }

    try {
      sendMoney(Number(amount));
      Alert.alert("Success", `Sent to ${name}`);
      goBack();
    } catch (err) {
      Alert.alert("Error", err.message);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Send Money</Text>

      <TextInput placeholder="Recipient name" style={styles.input} value={name} onChangeText={setName} />
      <TextInput placeholder="Account number" style={styles.input} value={accountNumber} keyboardType="number-pad" onChangeText={setAccountNumber} />
      <TextInput placeholder="Amount" style={styles.input} value={amount} keyboardType="number-pad" onChangeText={setAmount} />

      <ActionButton text="Send" color="#2196F3" onPress={handleSend} />
      <ActionButton text="Cancel" color="#999" onPress={goBack} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: "#fff",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 10,
    padding: 12,
    marginBottom: 12,
  },
});
