import React from "react";
import { View, Text, StyleSheet } from "react-native";

export default function AboutScreen() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>About My Bank App</Text>
      <Text style={styles.text}>Demo banking app using React Native & old Expo SDK.</Text>
      <Text style={styles.text}>- Deposit & Withdraw money</Text>
      <Text style={styles.text}>- Send money to other accounts</Text>
      <Text style={styles.text}>- Track all transactions</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: "#F3F6FF" },
  title: { fontSize: 24, fontWeight: "bold", marginBottom: 20 },
  text: { fontSize: 16, marginBottom: 10 },
});
