import React, { useState } from "react";
import { View, Text, FlatList, StyleSheet } from "react-native";
import { getAccount } from "../api/bankApi";

export default function TransactionsScreen() {
  const [transactions] = useState(getAccount().transactions);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Transactions</Text>
      {transactions.length === 0 && <Text style={{ textAlign: "center" }}>No transactions yet</Text>}
      <FlatList
        data={transactions}
        keyExtractor={(item, index) => index.toString()}
        renderItem={({ item }) => (
          <View style={styles.transaction}>
            <Text style={styles.type}>{item.type}</Text>
            <Text>${item.amount}</Text>
            {item.toAccount && <Text>To: {item.toAccount}</Text>}
            <Text style={styles.date}>{item.date}</Text>
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: "#F3F6FF" },
  title: { fontSize: 24, fontWeight: "bold", marginBottom: 20, textAlign: "center" },
  transaction: {
    padding: 15,
    borderRadius: 10,
    backgroundColor: "#fff",
    marginBottom: 10,
  },
  type: { fontWeight: "bold", fontSize: 16 },
  date: { color: "#999", marginTop: 5, fontSize: 12 },
});
