import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import apiData from '../api/api.json';

export default function HomeScreen() {
  const [balance, setBalance] = useState(apiData.balance);

  useEffect(() => {
    // You can implement a file update here if needed
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.balanceText}>Your Balance:</Text>
      <Text style={styles.amount}>${balance.toFixed(2)}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, justifyContent: 'center', alignItems: 'center' },
  balanceText: { fontSize: 20, marginBottom: 10 },
  amount: { fontSize: 36, fontWeight: 'bold' },
});
