import React, { useState } from "react";
import { View, Text, StyleSheet } from "react-native";
import BalanceCard from "../components/BalanceCard";
import ActionButton from "../components/ActionButton";
import { getAccount, depositMoney, withdrawMoney } from "../api/bankApi";

export default function HomeScreen({ navigation }) {
  const [account, setAccount] = useState(getAccount());

  return (
    <View style={styles.container}>
      <Text style={styles.title}>My Bank</Text>

      <BalanceCard name={account.name} balance={account.balance} />

      <View style={styles.actions}>
        <ActionButton
          text="Deposit $100"
          color="#4CAF50"
          onPress={() => setAccount({ ...depositMoney(100) })}
        />

        <ActionButton
          text="Withdraw $50"
          color="#FF9800"
          onPress={() => setAccount({ ...withdrawMoney(50) })}
        />

        <ActionButton
          text="Send Money"
          color="#2196F3"
          onPress={() => navigation.navigate("SendMoney")}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F3F6FF",
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: "bold",
    textAlign: "center",
    marginVertical: 20,
  },
  actions: {
    marginTop: 30,
  },
});
